import React from 'react';

const RequestRefund = () => {
    return (
        <div className="min-h-screen bg-white text-black py-10 px-4 max-w-4xl mx-auto">
            <h1 className="text-3xl font-bold text-pink-600 mb-6">How do I request a refund?</h1>

            <p className="mb-4">
                You can request a refund easily through our website. Just follow the steps below to process your request online.
                Please note that eligibility for refunds depends on your fare type and booking conditions.
            </p>

            <ul className="list-disc pl-6 space-y-2 text-gray-700">
                <li>Log in to your account using your credentials.</li>
                <li>Go to the <strong>My Bookings</strong> section.</li>
                <li>Select the booking you'd like to request a refund for.</li>
                <li>Click on <strong>Request Refund</strong> and follow the guided steps.</li>
            </ul>

            <p className="mt-6 text-sm text-gray-500">
                If you're unable to complete the refund process online or need immediate assistance, please contact our support team via live chat or phone â€” available 24/7.
            </p>
        </div>
    );
};

export default RequestRefund;
