import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';  // Add the Link import here


const faqAnswers = {
  'change-booking': {
    question: 'How do I change my booking?',
    answer: `
      To change your booking, go to your Dashboard > Bookings. 
      Click "Edit" next to the booking you want to change. 
      You can update dates, destinations, or traveler info from there.
    `,
    services: [
      { name: 'Change Booking Tool', link: '/edit-booking' },
      { name: 'Live Chat Support', link: '/chat' },
      { name: 'Email Support', link: 'mailto:support@example.com' }
    ]
  },
  'refund': {
    question: 'How do I request a refund?',
    answer: `
      To request a refund, go to your account > Order History and click "Refund".
      Fill out the form and submit. Our team will process it within 5-7 business days.
    `,
    services: [
      { name: 'Refund Form', link: '/refund-request' },
      { name: 'Support Center', link: '/help' }
    ]
  },
  'flight-status': {
    question: 'How can I check my flight status?',
    answer: `
      To check your flight status, visit the Flight Status page on the website. 
      Enter your flight number or route to get the latest updates.
    `,
    services: [
      { name: 'Flight Status', link: '/flight-status' },
      { name: 'Customer Support', link: '/chat' }
    ]
  },
  'account-info': {
    question: 'How do I update my account information?',
    answer: `
      To update your account information, go to your Dashboard > Account Settings. 
      You can modify personal details, password, or contact information.
    `,
    services: [
      { name: 'Account Settings', link: '/account-settings' },
      { name: 'Contact Support', link: '/chat' }
    ]
  },
  'booking-modifications': {
    question: 'How can I modify my booking?',
    answer: `
      To modify your booking, visit your Dashboard > Bookings. 
      Select the booking you want to change and click "Modify". You can update dates, routes, or passengers.
    `,
    services: [
      { name: 'Modify Booking', link: '/modify-booking' },
      { name: 'Support Center', link: '/help' }
    ]
  }
};

export default function FAQDetail() {
  const { questionId } = useParams();
  const faq = faqAnswers[questionId];

  if (!faq) {
    return <div className="p-10 text-center text-red-600">Question not found</div>;
  }

  return (
    <>
      <Header />
      <main className="max-w-3xl mx-auto py-10 px-4 text-gray-800">
        {/* Back to Help Center Link */}
        <Link to="/help-center" className="text-blue-600 underline mb-6">‚Üê Back to Help Center</Link>
        
        <h1 className="text-3xl font-bold mb-6">{faq.question}</h1>
        <p className="mb-6 text-lg">{faq.answer}</p>
        
        <h2 className="text-xl font-semibold mt-8 mb-2">Helpful Services</h2>
        <ul className="space-y-4">
          {faq.services.map((service, idx) => (
            <li key={idx}>
              <Link to={service.link} className="w-full">
                <button className="w-full py-2 px-4 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition duration-300">
                  {service.name}
                </button>
              </Link>
            </li>
          ))}
        </ul>
      </main>
      <Footer />
    </>
  );
}
