import { useParams, Link } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';

const faqAnswers = {
  'change-booking': {
    question: 'How do I change my booking?',
    answer: `To change your booking, go to your Dashboard > Bookings. Click "Edit" next to the booking you want to change. You can update dates, destinations, or traveler info from there.`,
    services: [
      { name: 'Change Booking Tool', link: '/edit-booking' },
      { name: 'Live Chat Support', link: '/chat' },
      { name: 'Email Support', link: 'mailto:support@example.com' }
    ]
  },
  'request-refund': {
    question: 'How do I request a refund?',
    answer: `To request a refund, go to your account > Order History and click "Refund". Fill out the form and submit. Our team will process it within 5-7 business days.`,
    services: [
      { name: 'Refund Form', link: '/refund-request' },
      { name: 'Support Center', link: '/help' }
    ]
  },
  'flight-status': {
    question: 'How do I check my flight status?',
    answer: `You can check your flight status under Dashboard > Flight Status or use the Flight Tracker page.`,
    services: [
      { name: 'Flight Tracker', link: '/flight-status' },
      { name: 'Live Chat Support', link: '/chat' }
    ]
  },
  'account-access': {
    question: 'I can’t access my account. What should I do?',
    answer: `Try resetting your password from the login page. If the issue persists, contact our support team for help recovering your account.`,
    services: [
      { name: 'Reset Password', link: '/reset-password' },
      { name: 'Account Recovery Help', link: '/account-help' }
    ]
  },
  'baggage-policy': {
    question: 'What is the baggage policy?',
    answer: `Each passenger is allowed one cabin bag (max 10kg) and one checked bag (max 20kg). Additional baggage fees may apply depending on your fare class and destination.`,
    services: [
      { name: 'Add Extra Baggage', link: '/add-baggage' },
      { name: 'Baggage Size Guide', link: '/baggage-guide' }
    ]
  }
};
