import { useParams, Link } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';

const faqAnswers = {
  'change-booking': {
    question: 'How do I change my booking?',
    answer: `
      To change your booking, go to your Dashboard > Bookings. 
      Click "Edit" next to the booking you want to change. 
      You can update dates, destinations, or traveler info from there.
    `,
    services: [
      { name: 'Change Booking Tool', link: '/edit-booking' },
      { name: 'Live Chat Support', link: '/chat' },
      { name: 'Email Support', link: 'mailto:support@example.com' },
    ],
  },
  'refund': {
    question: 'How do I request a refund?',
    answer: `
      To request a refund, go to your account > Order History and click "Refund".
      Fill out the form and submit. Our team will process it within 5-7 business days.
    `,
    services: [
      { name: 'Refund Form', link: '/refund-request' },
      { name: 'Support Center', link: '/help' },
    ],
  },
  'flight-status': {
    question: 'How do I check my flight status?',
    answer: `
      You can check the status of your flight on the Flight Status page. 
      Simply enter your flight number, and you'll get the latest updates.
    `,
    services: [
      { name: 'Flight Status Tool', link: '/flight-status' },
      { name: 'Customer Support', link: '/help' },
    ],
  },
  'account': {
    question: 'How can I access my account?',
    answer: `
      To access your account, click on "Login" at the top right of the page. 
      Enter your credentials, and you'll be able to view your bookings and personal details.
    `,
    services: [
      { name: 'Login', link: '/login' },
      { name: 'Register', link: '/register' },
      { name: 'Forgot Password', link: '/forgot-password' },
    ],
  },
  'booking-modifications': {
    question: 'How do I modify my booking?',
    answer: `
      To modify your booking, go to your Dashboard > Bookings, 
      select the booking you want to modify, and click on "Modify Booking".
      You can update the details like dates, flight class, etc.
    `,
    services: [
      { name: 'Modify Booking', link: '/modify-booking' },
      { name: 'Live Chat Support', link: '/chat' },
    ],
  },
  'cancellation': {
    question: 'How do I cancel my booking?',
    answer: `
      To cancel your booking, go to your Dashboard > Bookings and click "Cancel" next to the booking. 
      Follow the prompts to complete the cancellation process.
    `,
    services: [
      { name: 'Cancel Booking', link: '/cancel-booking' },
      { name: 'Refund Request', link: '/refund-request' },
    ],
  },
  'customer-support': {
    question: 'How can I contact customer support?',
    answer: `
      You can reach customer support via our "Contact Us" page, 
      or by using the Live Chat Support tool. 
      Alternatively, you can email us at support@example.com.
    `,
    services: [
      { name: 'Live Chat Support', link: '/chat' },
      { name: 'Contact Us', link: '/contact-us' },
      { name: 'Email Support', link: 'mailto:support@example.com' },
    ],
  },
  // Add more FAQs and services as needed
};

export default function FAQDetail() {
  const { questionId } = useParams();  // Get the questionId from the URL
  const faq = faqAnswers[questionId];  // Get the FAQ details based on the questionId

  // If no FAQ is found, show a "not found" message
  if (!faq) {
    return <div className="p-10 text-center text-red-600">Question not found</div>;
  }

  return (
    <>
      <Header />
      <main className="max-w-3xl mx-auto py-10 px-4 text-gray-800">
        {/* Link back to Help Center */}
        <Link to="/help-center" className="text-blue-600 underline mb-6">
          ← Back to Help Center
        </Link>

        {/* Display the question */}
        <h1 className="text-3xl font-bold mb-6">{faq.question}</h1>
        
        {/* Display the answer */}
        <p className="mb-6 text-lg">{faq.answer}</p>

        {/* List of helpful services as buttons */}
        <h2 className="text-xl font-semibold mt-8 mb-2">Helpful Services</h2>
        <ul className="space-y-4">
          {faq.services.map((service, idx) => (
            <li key={idx}>
              <Link to={service.link} className="w-full">
                <button className="w-full py-2 px-4 bg-blue-600 text-white font-semibold rounded-lg hover:bg-blue-700 transition duration-300">
                  {service.name}
                </button>
              </Link>
            </li>
          ))}
        </ul>
      </main>
      <Footer />
    </>
  );
}
