import { useParams } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';

const faqAnswers = {
  'change-booking': {
    question: 'How do I change my booking?',
    answer: `
      To change your booking, go to your Dashboard > Bookings. 
      Click "Edit" next to the booking you want to change. 
      You can update dates, destinations, or traveler info from there.
    `,
    services: [
      { name: 'Change Booking Tool', link: '/edit-booking' },
      { name: 'Live Chat Support', link: '/chat' },
      { name: 'Email Support', link: 'mailto:support@example.com' }
    ]
  },
  'refund': {
    question: 'How do I request a refund?',
    answer: `
      To request a refund, go to your account > Order History and click "Refund".
      Fill out the form and submit. Our team will process it within 5-7 business days.
    `,
    services: [
      { name: 'Refund Form', link: '/refund-request' },
      { name: 'Support Center', link: '/help' }
    ]
  },
  // Add more as needed
};

export default function FAQDetail() {
  const { questionId } = useParams();
  const faq = faqAnswers[questionId];

  if (!faq) {
    return <div className="p-10 text-center text-red-600">Question not found</div>;
  }

  return (
    <>
      <Header />
      <main className="max-w-3xl mx-auto py-10 px-4 text-gray-800">
        <h1 className="text-3xl font-bold mb-6">{faq.question}</h1>
        <p className="mb-6 text-lg">{faq.answer}</p>
        <h2 className="text-xl font-semibold mt-8 mb-2">Helpful Services</h2>
        <ul className="list-disc pl-6 space-y-2">
          {faq.services.map((service, idx) => (
            <li key={idx}>
              <a href={service.link} className="text-blue-600 underline">
                {service.name}
              </a>
            </li>
          ))}
        </ul>
      </main>
      <Footer />
    </>
  );
}
