import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './pages/Home';
import SignUp from './components/SignUp';
import Register from './components/Register';
import HelpCenter from './pages/HelpCenter';
import CheckInBooking from './pages/CheckInBooking';
import Services from './pages/Services';
import FAQPage from './pages/FAQPage';
import FAQDetail from './pages/FAQDetails';

function AppContent({ showSignUp, setShowSignUp, showRegister, setShowRegister }) {
  const location = useLocation();
  const isHelpCenter = location.pathname === '/help-center';

  return (
    <div className="min-h-screen flex flex-col">
      {/* Conditionally render Header */}
      {!isHelpCenter && <Header onSignUpClick={() => setShowSignUp(true)} />}

      <main className="flex-grow">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/signup" element={<SignUp />} />
          <Route path="/register" element={<Register />} />
          <Route path="/help-center" element={<HelpCenter />} />
          <Route path="/check-in-booking" element={<CheckInBooking />} />
          <Route path="/services" element={<Services />} />
          <Route path="/faq" element={<FAQPage />} />
          <Route path="/faq/:questionId" element={<FAQDetail />} />
        </Routes>
      </main>

      {/* Conditionally render Footer */}
      {!isHelpCenter && <Footer />}

      {showSignUp && (
        <SignUp
          onClose={() => setShowSignUp(false)}
          onRegisterClick={() => {
            setShowSignUp(false);
            setShowRegister(true);
          }}
        />
      )}

      {showRegister && <Register onClose={() => setShowRegister(false)} />}
    </div>
  );
}

function App() {
  const [showSignUp, setShowSignUp] = useState(false);
  const [showRegister, setShowRegister] = useState(false);

  return (
    <Router>
      <AppContent
        showSignUp={showSignUp}
        setShowSignUp={setShowSignUp}
        showRegister={showRegister}
        setShowRegister={setShowRegister}
      />
    </Router>
  );
}

export default App;
