import React from 'react';

const hotels = [
  {
    city: 'Paris, France',
    image: '/images/paris.jpg',
    description: 'Experience the romantic charm of Paris with luxury accommodations and breathtaking views of the Eiffel Tower.',
    amenities: ['Free Wi-Fi', '24/7 Concierge Service', 'Indoor Pool'],
    priceRange: 'From $150 per night',
  },
  {
    city: 'Budapest, Hungary',
    image: '/images/budapest.jpg',
    description: 'Discover the beauty of Budapest, where historic architecture meets modern amenities in the heart of Europe.',
    amenities: ['Spa Services', 'City Tours', 'Free Breakfast'],
    priceRange: 'From $120 per night',
  },
  {
    city: 'New York, USA',
    image: '/images/newyork.jpg',
    description: 'Stay in the vibrant city of New York, offering a wide range of attractions from Broadway to Central Park.',
    amenities: ['Fitness Center', 'Room Service', 'Complimentary Shuttle'],
    priceRange: 'From $200 per night',
  },
  {
    city: 'Rome, Italy',
    image: '/images/rome.jpg',
    description: 'Immerse yourself in the ancient wonders of Rome, with easy access to the Colosseum and the Vatican.',
    amenities: ['Historical Tours', 'On-site Dining', 'Luxury Spa'],
    priceRange: 'From $180 per night',
  },
  {
    city: 'Berlin, Germany',
    image: '/images/berlin.jpg',
    description: 'Explore Berlin, a hub for modern art, history, and culture, all while enjoying world-class accommodations.',
    amenities: ['Art Galleries', 'Pet-Friendly', 'Complimentary Parking'],
    priceRange: 'From $130 per night',
  },
  {
    city: 'Stockholm, Sweden',
    image: '/images/stockholm.jpg',
    description: 'Enjoy the elegance of Stockholm, with its scenic waterfronts, stylish hotels, and rich Scandinavian history.',
    amenities: ['Free Breakfast', 'Luxury Lounge', 'Airport Shuttle'],
    priceRange: 'From $140 per night',
  },
];

const Hotels = () => {
  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-4xl font-bold text-center mb-12 text-blue-800">Explore Our Luxury Hotels</h1>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {hotels.map((hotel, index) => (
          <div key={index} className="bg-white rounded-xl shadow-lg overflow-hidden transition-transform transform hover:scale-105 hover:shadow-xl">
            <img src={hotel.image} alt={hotel.city} className="w-full h-56 object-cover transition-opacity duration-500 hover:opacity-80" />
            
            <div className="p-6">
              <h2 className="text-2xl font-semibold text-center text-gray-800 mb-3">{hotel.city}</h2>
              <p className="text-gray-600 text-sm text-center mb-4">{hotel.description}</p>

              <div className="flex justify-center mb-4">
                <ul className="list-disc text-gray-700 text-sm">
                  {hotel.amenities.map((amenity, index) => (
                    <li key={index} className="text-center">{amenity}</li>
                  ))}
                </ul>
              </div>

              <div className="flex justify-between items-center">
                <span className="text-xl font-semibold text-gray-800">{hotel.priceRange}</span>
                <button className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition duration-300">
                  Book Now
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Hotels;
