import React, { useState } from 'react';

// Sample data for hotels
const hotels = [
  {
    city: 'Paris, France',
    images: ['/images/paris.jpg', '/images/paris2.jpg', '/images/paris3.jpg'],
    description: 'Romantic getaway in the heart of Paris with stunning views of the Eiffel Tower.',
    amenities: ['Free Wi-Fi', 'Swimming Pool', 'Fitness Center', 'Restaurant'],
    priceRange: '$150 per night',
    rating: 4.5,
    roomTypes: ['Standard Room', 'Luxury Suite'],
    availableRooms: 5,
    trending: true,
  },
  {
    city: 'New York, USA',
    images: ['/images/newyork.jpg', '/images/newyork2.jpg', '/images/newyork3.jpg'],
    description: 'Explore the bustling city of New York with luxury accommodations and prime location.',
    amenities: ['Spa Services', 'City View', 'Free Breakfast', '24/7 Concierge'],
    priceRange: '$200 per night',
    rating: 4.7,
    roomTypes: ['Deluxe Room', 'Family Suite'],
    availableRooms: 8,
    trending: false,
  },
  // Add more hotels as needed
];

const HotelPage = () => {
  const [selectedRoom, setSelectedRoom] = useState('');
  const [checkInDate, setCheckInDate] = useState('');
  const [checkOutDate, setCheckOutDate] = useState('');
  const [guests, setGuests] = useState(1);
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearch = (e) => {
    e.preventDefault();
    // You can implement real search functionality here
    console.log(`Searching for hotels in: ${searchQuery}`);
  };

  const handleBookNow = (hotel) => {
    alert(`Booking selected for ${hotel.city}`);
    // Implement further booking logic (e.g., redirect to booking form)
  };

  return (
    <div className="flex min-h-screen">
      {/* Sidebar for account, language, and favorite hotels */}
      <div className="w-64 bg-gray-800 text-white p-6">
        <h2 className="text-xl font-semibold mb-4">Your Account</h2>
        <div className="mb-6">
          <h3 className="text-lg font-semibold text-gray-400 mb-2">Favorites</h3>
          <ul className="space-y-2">
            {/* Dynamically render favorite hotels here */}
            <li className="text-sm">Hotel in Paris</li>
            <li className="text-sm">Hotel in Rome</li>
            <li className="text-sm">Hotel in Tokyo</li>
          </ul>
        </div>
        
        <div className="mb-6">
          <h3 className="text-lg font-semibold text-gray-400 mb-2">Account Settings</h3>
          <ul className="space-y-2">
            {/* Add Account-related links here */}
            <li className="text-sm">Profile</li>
            <li className="text-sm">Booking History</li>
            <li className="text-sm">Payment Methods</li>
          </ul>
        </div>
        
        <div className="mb-6">
          <h3 className="text-lg font-semibold text-gray-400 mb-2">Language</h3>
          {/* Add language selection here */}
          <select className="w-full p-2 rounded-md">
            <option value="en">English</option>
            <option value="fr">Français</option>
            <option value="de">Deutsch</option>
            {/* Add more languages */}
          </select>
        </div>

        {/* Sign In Promotion */}
        <div className="mt-8 text-center">
          <button className="bg-blue-600 hover:bg-blue-700 w-full py-2 rounded-md text-white">
            Sign In - Get 10% Off!
          </button>
        </div>
      </div>

      {/* Main content area */}
      <div className="flex-grow bg-gray-100 p-6">
        {/* Search bar */}
        <form onSubmit={handleSearch} className="bg-white p-4 shadow-md rounded-lg mb-8">
          <h1 className="text-2xl font-bold text-center text-blue-800 mb-4">Find Your Perfect Hotel</h1>
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
            <div>
              <input
                type="text"
                className="w-full p-2 border rounded-md"
                placeholder="Search destination"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
            </div>
            <div>
              <input
                type="date"
                className="w-full p-2 border rounded-md"
                value={checkInDate}
                onChange={(e) => setCheckInDate(e.target.value)}
              />
            </div>
            <div>
              <input
                type="date"
                className="w-full p-2 border rounded-md"
                value={checkOutDate}
                onChange={(e) => setCheckOutDate(e.target.value)}
              />
            </div>
            <div>
              <input
                type="number"
                className="w-full p-2 border rounded-md"
                min="1"
                placeholder="Adults"
                value={guests}
                onChange={(e) => setGuests(e.target.value)}
              />
            </div>
            <div>
              <button
                type="submit"
                className="bg-blue-600 text-white py-2 px-4 rounded-md w-full mt-2 sm:col-span-2"
              >
                Search
              </button>
            </div>
          </div>
        </form>

        {/* Hotel Offers Section */}
        <section className="mb-12">
          <h2 className="text-3xl font-semibold text-gray-800 mb-4">Special Offers</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {hotels.map(
              (hotel, index) =>
                hotel.trending && (
                  <div key={index} className="bg-white rounded-xl shadow-md overflow-hidden hover:shadow-xl">
                    <img
                      src={hotel.images[0]}
                      alt={hotel.city}
                      className="w-full h-56 object-cover"
                    />
                    <div className="p-4">
                      <h3 className="font-semibold text-xl text-gray-800 mb-2">{hotel.city}</h3>
                      <p className="text-gray-600 text-sm mb-4">{hotel.description}</p>
                      <span className="block font-semibold text-lg text-gray-800">{hotel.priceRange}</span>
                      <button
                        onClick={() => handleBookNow(hotel)}
                        className="bg-blue-600 text-white py-2 px-4 rounded-md mt-4 w-full"
                      >
                        Book Now
                      </button>
                    </div>
                  </div>
                )
            )}
          </div>
        </section>

        {/* Trending Places Section */}
        <section className="mb-12">
          <h2 className="text-3xl font-semibold text-gray-800 mb-4">Trending Places</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {hotels.map(
              (hotel, index) =>
                !hotel.trending && (
                  <div key={index} className="bg-white rounded-xl shadow-md overflow-hidden hover:shadow-xl">
                    <img
                      src={hotel.images[0]}
                      alt={hotel.city}
                      className="w-full h-56 object-cover"
                    />
                    <div className="p-4">
                      <h3 className="font-semibold text-xl text-gray-800 mb-2">{hotel.city}</h3>
                      <p className="text-gray-600 text-sm mb-4">{hotel.description}</p>
                      <span className="block font-semibold text-lg text-gray-800">{hotel.priceRange}</span>
                      <button
                        onClick={() => handleBookNow(hotel)}
                        className="bg-blue-600 text-white py-2 px-4 rounded-md mt-4 w-full"
                      >
                        Book Now
                      </button>
                    </div>
                  </div>
                )
            )}
          </div>
        </section>

        {/* Browse by Property Type */}
        <section className="mb-12">
          <h2 className="text-3xl font-semibold text-gray-800 mb-4">Browse by Property Type</h2>
          <div className="flex space-x-8">
            <button className="bg-gray-300 text-gray-700 px-6 py-3 rounded-md hover:bg-gray-400">Resorts</button>
            <button className="bg-gray-300 text-gray-700 px-6 py-3 rounded-md hover:bg-gray-400">Apartments</button>
            <button className="bg-gray-300 text-gray-700 px-6 py-3 rounded-md hover:bg-gray-400">Beach Hotels</button>
            <button className="bg-gray-300 text-gray-700 px-6 py-3 rounded-md hover:bg-gray-400">Family Hotels</button>
          </div>
        </section>
      </div>
    </div>
  );
};

export default HotelPage;
