import React, { useState } from "react";

// Sample hotel data
const hotel = {
  name: "Luxury Resort",
  location: "Maldives",
  images: [
    "/images/hotel-main.jpg",
    "/images/hotel-rooms.jpg",
    "/images/hotel-amenities.jpg",
  ],
  description:
    "Experience the epitome of luxury at Luxury Resort. Unwind by the beach, indulge in world-class amenities, and enjoy a stay filled with unforgettable moments.",
  amenities: [
    "Free Wi-Fi",
    "Spa Services",
    "Infinity Pool",
    "Beachfront",
    "Fitness Center",
    "Room Service",
  ],
  rooms: [
    {
      type: "Single Room",
      price: "$150 per night",
      available: true,
      description:
        "A cozy single room with a beautiful ocean view. Perfect for solo travelers.",
      images: ["/images/room1.jpg"],
    },
    {
      type: "Double Room",
      price: "$250 per night",
      available: true,
      description:
        "A spacious room with a balcony and stunning beach views. Ideal for couples.",
      images: ["/images/room2.jpg"],
    },
    {
      type: "Suite",
      price: "$450 per night",
      available: false,
      description:
        "Luxurious suite with a private pool and panoramic views. For those looking for ultimate comfort.",
      images: ["/images/room3.jpg"],
    },
  ],
};

const HotelPage = () => {
  const [selectedRoom, setSelectedRoom] = useState(null);
  const [checkInDate, setCheckInDate] = useState("");
  const [checkOutDate, setCheckOutDate] = useState("");
  const [guests, setGuests] = useState(1);
  const [promoCode, setPromoCode] = useState("");

  const handleRoomSelect = (room) => {
    setSelectedRoom(room);
  };

  const handleBooking = () => {
    alert("Booking Successful!");
  };

  return (
    <div className="hotel-page">
      {/* Hero Section */}
      <section className="hero-section relative">
        <img src={hotel.images[0]} alt="Hotel" className="w-full h-[400px] object-cover" />
        <div className="absolute inset-0 bg-black opacity-50"></div>
        <div className="absolute inset-0 flex justify-center items-center text-center text-white p-6">
          <div>
            <h1 className="text-5xl font-bold">{hotel.name}</h1>
            <p className="text-lg mt-2">{hotel.location}</p>
            <p className="text-xl mt-4">{hotel.description}</p>
          </div>
        </div>
      </section>

      {/* Navigation Bar */}
      <nav className="bg-gray-900 text-white p-4">
        <ul className="flex justify-center space-x-8">
          <li><a href="#rooms" className="hover:text-sky-400">Rooms</a></li>
          <li><a href="#amenities" className="hover:text-sky-400">Amenities</a></li>
          <li><a href="#booking" className="hover:text-sky-400">Booking</a></li>
          <li><a href="#reviews" className="hover:text-sky-400">Reviews</a></li>
          <li><a href="#contact" className="hover:text-sky-400">Contact</a></li>
        </ul>
      </nav>

      {/* Rooms & Pricing */}
      <section id="rooms" className="rooms-section py-10 px-6">
        <h2 className="text-3xl font-semibold text-center mb-8">Rooms & Pricing</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {hotel.rooms.map((room, index) => (
            <div key={index} className="room-card bg-white rounded-lg shadow-lg overflow-hidden">
              <img
                src={room.images[0]}
                alt={room.type}
                className="w-full h-56 object-cover"
              />
              <div className="p-6 text-center">
                <h3 className="text-xl font-bold">{room.type}</h3>
                <p className="mt-2 text-sm text-gray-600">{room.description}</p>
                <p className="mt-2 text-lg font-semibold">{room.price}</p>
                {room.available ? (
                  <button
                    className="mt-4 bg-sky-500 text-white py-2 px-6 rounded-lg hover:bg-sky-600"
                    onClick={() => handleRoomSelect(room)}
                  >
                    Select Room
                  </button>
                ) : (
                  <span className="mt-4 text-red-500">Not Available</span>
                )}
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Booking System */}
      {selectedRoom && (
        <section id="booking" className="booking-section bg-gray-100 py-10 px-6">
          <h2 className="text-3xl font-semibold text-center mb-8">Book Your Stay</h2>
          <form onSubmit={handleBooking} className="max-w-md mx-auto bg-white p-6 rounded-lg shadow-lg">
            <label className="block mb-4">
              <span className="text-gray-700">Check-in Date</span>
              <input
                type="date"
                className="w-full mt-1 p-2 border border-gray-300 rounded-lg"
                value={checkInDate}
                onChange={(e) => setCheckInDate(e.target.value)}
              />
            </label>
            <label className="block mb-4">
              <span className="text-gray-700">Check-out Date</span>
              <input
                type="date"
                className="w-full mt-1 p-2 border border-gray-300 rounded-lg"
                value={checkOutDate}
                onChange={(e) => setCheckOutDate(e.target.value)}
              />
            </label>
            <label className="block mb-4">
              <span className="text-gray-700">Number of Guests</span>
              <input
                type="number"
                min="1"
                className="w-full mt-1 p-2 border border-gray-300 rounded-lg"
                value={guests}
                onChange={(e) => setGuests(e.target.value)}
              />
            </label>
            <label className="block mb-4">
              <span className="text-gray-700">Promo Code</span>
              <input
                type="text"
                className="w-full mt-1 p-2 border border-gray-300 rounded-lg"
                value={promoCode}
                onChange={(e) => setPromoCode(e.target.value)}
              />
            </label>
            <button
              type="submit"
              className="w-full bg-sky-500 text-white py-3 rounded-lg hover:bg-sky-600"
            >
              Book Now
            </button>
          </form>
          <div className="mt-4 text-center">
            <p>Room Rate: {selectedRoom.price} x {guests} guests</p>
            <p>Total: ${parseFloat(selectedRoom.price.replace('$', '')) * guests}</p>
          </div>
        </section>
      )}

      {/* Hotel Amenities */}
      <section id="amenities" className="amenities-section py-10 px-6 bg-gray-50">
        <h2 className="text-3xl font-semibold text-center mb-8">Hotel Amenities</h2>
        <ul className="list-disc pl-6">
          {hotel.amenities.map((amenity, index) => (
            <li key={index} className="text-lg">{amenity}</li>
          ))}
        </ul>
      </section>

      {/* Customer Reviews */}
      <section id="reviews" className="reviews-section py-10 px-6">
        <h2 className="text-3xl font-semibold text-center mb-8">Customer Reviews</h2>
        {/* Add reviews here */}
      </section>

      {/* Contact Section */}
      <section id="contact" className="contact-section py-10 px-6 bg-gray-100">
        <h2 className="text-3xl font-semibold text-center mb-8">Contact Us</h2>
        <form className="max-w-md mx-auto bg-white p-6 rounded-lg shadow-lg">
          <input type="text" placeholder="Your Name" className="w-full p-2 mb-4 border border-gray-300 rounded-lg" />
          <input type="email" placeholder="Your Email" className="w-full p-2 mb-4 border border-gray-300 rounded-lg" />
          <textarea placeholder="Your Message" className="w-full p-2 mb-4 border border-gray-300 rounded-lg"></textarea>
          <button type="submit" className="w-full bg-sky-500 text-white py-3 rounded-lg hover:bg-sky-600">Submit</button>
        </form>
      </section>
    </div>
  );
};

export default HotelPage;
