import React, { useState } from 'react';

const hotels = [
  {
    city: 'Paris, France',
    images: ['/images/paris.jpg', '/images/paris2.jpg', '/images/paris3.jpg'],
    description: 'Experience the romantic charm of Paris with luxury accommodations and breathtaking views of the Eiffel Tower.',
    amenities: ['Free Wi-Fi', '24/7 Concierge Service', 'Indoor Pool', 'Fitness Center'],
    priceRange: 'From $150 per night',
    rating: 4.5,
    roomTypes: ['Standard Room', 'Luxury Suite', 'Penthouse Suite'],
    availableRooms: 5,
  },
  {
    city: 'Budapest, Hungary',
    images: ['/images/budapest.jpg', '/images/budapest2.jpg', '/images/budapest3.jpg'],
    description: 'Discover the beauty of Budapest, where historic architecture meets modern amenities in the heart of Europe.',
    amenities: ['Spa Services', 'City Tours', 'Free Breakfast', 'Outdoor Pool'],
    priceRange: 'From $120 per night',
    rating: 4.7,
    roomTypes: ['Deluxe Room', 'Family Suite'],
    availableRooms: 3,
  },
  // Add more hotels as needed
];

const Hotels = () => {
  const [selectedRoom, setSelectedRoom] = useState('');
  const [checkInDate, setCheckInDate] = useState('');
  const [checkOutDate, setCheckOutDate] = useState('');
  const [guests, setGuests] = useState(1);

  const handleBookNow = (hotel) => {
    alert(`You have selected ${hotel.city} for your stay! Room: ${selectedRoom}, Check-in: ${checkInDate}, Check-out: ${checkOutDate}, Guests: ${guests}`);
    // Implement further booking logic here (e.g., integrate with payment gateway or reservation system)
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-4xl font-bold text-center mb-12 text-blue-800">Explore Our Luxury Hotels</h1>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
        {hotels.map((hotel, index) => (
          <div key={index} className="bg-white rounded-xl shadow-lg overflow-hidden transition-transform transform hover:scale-105 hover:shadow-xl">
            <div className="relative">
              {/* Carousel of hotel images */}
              <div className="relative h-56 sm:h-64 lg:h-72">
                <div className="absolute inset-0 overflow-hidden">
                  <div className="flex transition-all duration-500">
                    {hotel.images.map((image, idx) => (
                      <img
                        key={idx}
                        src={image}
                        alt={`${hotel.city} ${idx + 1}`}
                        className="w-full h-full object-cover flex-shrink-0 transition-opacity duration-500 hover:opacity-80"
                      />
                    ))}
                  </div>
                </div>
              </div>
            </div>

            <div className="p-6">
              <h2 className="text-2xl font-semibold text-center text-gray-800 mb-3">{hotel.city}</h2>
              <p className="text-gray-600 text-sm text-center mb-4">{hotel.description}</p>

              <div className="flex justify-center mb-4">
                <ul className="list-disc text-gray-700 text-sm">
                  {hotel.amenities.map((amenity, index) => (
                    <li key={index} className="text-center">{amenity}</li>
                  ))}
                </ul>
              </div>

              <div className="flex justify-between items-center mb-4">
                <span className="text-xl font-semibold text-gray-800">{hotel.priceRange}</span>
                <span className="text-yellow-500 text-lg font-semibold">{`★ ${hotel.rating}`}</span>
              </div>

              <div className="mb-4">
                <h3 className="font-semibold text-lg text-gray-800">Room Types:</h3>
                <select
                  className="w-full bg-white text-gray-800 p-2 rounded-lg border"
                  value={selectedRoom}
                  onChange={(e) => setSelectedRoom(e.target.value)}
                >
                  <option value="">Select Room</option>
                  {hotel.roomTypes.map((room, index) => (
                    <option key={index} value={room}>
                      {room}
                    </option>
                  ))}
                </select>
              </div>

              <div className="mb-4 grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-gray-700">Check-In</label>
                  <input
                    type="date"
                    className="w-full p-2 border rounded-lg"
                    value={checkInDate}
                    onChange={(e) => setCheckInDate(e.target.value)}
                  />
                </div>
                <div>
                  <label className="block text-gray-700">Check-Out</label>
                  <input
                    type="date"
                    className="w-full p-2 border rounded-lg"
                    value={checkOutDate}
                    onChange={(e) => setCheckOutDate(e.target.value)}
                  />
                </div>
              </div>

              <div className="mb-4">
                <label className="block text-gray-700">Guests</label>
                <input
                  type="number"
                  min="1"
                  className="w-full p-2 border rounded-lg"
                  value={guests}
                  onChange={(e) => setGuests(e.target.value)}
                />
              </div>

              <div className="flex justify-between items-center">
                <span className="text-gray-600">{`${hotel.availableRooms} Rooms Available`}</span>
                <button
                  onClick={() => handleBookNow(hotel)}
                  className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition duration-300"
                >
                  Book Now
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Hotels;
